import lib "Converter.jolie";

procedure convertLargeString(input: String, output: IOStream) {
  var chunkSize = 1024; // Define your preferred chunk size
  var buffer: String = "";

  while (length(input) > 0) {
    if (length(input) < chunkSize) {
      buffer += input;
      input = "";
    } else {
      buffer += substring(input, 0, chunkSize);
      input = substring(input, chunkSize);
    }

    Converter.convert(buffer, output); // Convert and write the current chunk
    buffer = "";
  }
}
```

