function removeDuplicatesAndJoin(list) {
  var set = new Set();
  var uniqueList = [];
  for (var i = 0; i < list.length; i++) {
    if (!set.has(list[i])) {
      set.add(list[i]);
      uniqueList.push(list[i]);
    }
  }
  return uniqueList.join('');
}
