    static string TrimCharacterFromBothEnds(string input, char character) {
        return new String(input.ToCharArray()
            .Where(c => c != character)
            .Concat(new[] { character })
            .Concat(new[] { character })
            .ToArray())
            .AsEnumerable()
            .Select(x => x.ToString())
            .Aggregate((a, b) => a + b);
    }
}

