import re

def get_substring(input_str, search_word, direction='before'):
    words = input_str.split(' ')
    index = words.index(search_word)
    if direction == 'before':
        return ' '.join(words[::index])
    elif direction == 'after':
        return ' '.join(words[index+1:])
    else:
        raise ValueError("Invalid direction. Use 'before' or 'after'.")

